@section('_scripts')
    <script>
        function clickYes(){
            $('#yes').removeClass('hidden');
            $('#no').addClass('hidden');
            $('#notsure').addClass('hidden');
        }
        function clickNo(){
            $('#no').removeClass('hidden');
            $('#yes').addClass('hidden');
            $('#notsure').addClass('hidden');
        }
        function clickNotSure(){
            $('#no').addClass('hidden');
            $('#yes').addClass('hidden');
            $('#notsure').removeClass('hidden');
        }
    </script>
@endsection